import React, { useState } from "react";
import auth from "./firebase";
import {
  Button,
  Box,
  TextField,
  InputAdornment,
  IconButton,
  ThemeProvider,
  createMuiTheme,
  Container,
  Grid,
} from "@material-ui/core";
import { Visibility, VisibilityOff } from "@material-ui/icons";
import { makeStyles } from "@material-ui/core/styles";
import { blueGrey } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
  },
  content: {
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
  },
}));

var styles = StyleSheet.create({
  content:{
      flex:1,
      flexDirection:'row',
      alignItems:'center',
      justifyContent:'center'
},
});

const theme = createMuiTheme({
  palette: {
    primary: blueGrey,
  },
});

const Login = ({ setSession }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  // console.log(auth);
  const classes = useStyles();
  const [values, setValues] = useState(false);
  //   {
  //   showPassword: false,
  // });

  const handleLogin = async () => {
    try {
      const response = await auth.signInWithEmailAndPassword(
        username,
        password
      );
      // console.log(response)
      const { user } = response;
      console.log({ user });

      setSession({
        isLoggedIn: true,
        currentUser: user,
      });
    } catch (err) {
      console.log({ err });

      setSession({
        isLoggedIn: false,
        currentUser: null,
        errorMessage: err.message,
      });
    }
  };

  const handleUsername = (event) => {
    setUsername(event.target.value);
  };

  const handlePassword = (event) => {
    setPassword(event.target.value);
  };

  const handleRegister = () => {
    try {
      const response = auth.createUserWithEmailAndPassword(username, password);

      const { user } = response;

      setSession({
        isLoggedIn: true,
        currentUser: user,
      });
    } catch (err) {
      setSession({
        isLoggedIn: false,
        currentUser: null,
        errorMessage: err.message,
      });
    }
  };

  const handleClickShowPassword = () => {
    setValues({ showPassword: !values.showPassword });
  };

  const handleMouseDownPassword = (event: MouseEvent<HTMLButtonElement>) => {
    event.preventDefault();
  };

  return (
    // <div>
    //   <input type="email" onChange={handleUsername}></input>
    //   <input type="password" onChange={handlePassword}></input>
    //   <button onClick={handleLogin}>Login</button>
    //   <button onClick={handleRegister}>Register</button>
    // </div>
    <Container maxWidth="xs" style={{ backgroundColor: "red" }}>
      <Box style={{ backgroundColor: "white" }} className={styles.content}>
        <form noValidate autoComplete="off">
          <TextField
            className={classes.margin}
            id="input-with-icon-textfield"
            label="Email"
            fullWidth
            onChange={handleUsername}
          />

          <TextField
            className={classes.margin}
            id="input-with-icon-textfield"
            label="Password"
            type={values.showPassword ? "text" : "password"}
            value={values.password}
            onChange={handlePassword}
            fullWidth
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton
                    aria-label="toggle password visibility"
                    onClick={handleClickShowPassword}
                    onMouseDown={handleMouseDownPassword}
                    edge="end"
                  >
                    {values.showPassword ? <Visibility /> : <VisibilityOff />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />

          <ThemeProvider theme={theme}>
            <Button
              variant="contained"
              color="primary"
              className={classes.margin}
              fullWidth
            >
              Sign in
            </Button>
          </ThemeProvider>

          <p>
            Dont have account ? <a href="#">Sign up</a>
          </p>
          <hr></hr>
          <ThemeProvider theme={theme}>
            <Button
              variant="contained"
              color="primary"
              className={classes.margin}
              fullWidth
            >
              Facebook
            </Button>
          </ThemeProvider>
        </form>
      </Box>
      <Grid container spacing={3} className={classes.content}>
        <Grid item xs={12}>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
                className={classes.margin}
                id="input-with-icon-textfield"
                label="Email"
                fullWidth
                onChange={handleUsername}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                className={classes.margin}
                id="input-with-icon-textfield"
                label="Password"
                type={values.showPassword ? "text" : "password"}
                value={values.password}
                onChange={handlePassword}
                fullWidth
                InputProps={{
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton
                        aria-label="toggle password visibility"
                        onClick={handleClickShowPassword}
                        onMouseDown={handleMouseDownPassword}
                        edge="end"
                      >
                        {values.showPassword ? (
                          <Visibility />
                        ) : (
                          <VisibilityOff />
                        )}
                      </IconButton>
                    </InputAdornment>
                  ),
                }}
              />
            </Grid>
          </Grid>
        </Grid>
        <Grid item xs={12}>
          <Button color="secondary" fullWidth variant="contained">
            Log in
          </Button>
          <p>
            Dont have account ? <a href="#">Sign up</a>
          </p>
          <hr></hr>
        </Grid>
        <Grid item xs={12}></Grid>
      </Grid>
    </Container>
    // <div>
  );
};

export default Login;
