import React, { useState } from "react";
import auth from "./firebase";
import {
  Button,
  Box,
  TextField,
  InputAdornment,
  IconButton,
  ThemeProvider,
  createMuiTheme,
  Container,
  Grid,
} from "@material-ui/core";
